import { useState } from 'react'
import Header from './components/Header.js'
import Tasks from './components/Tasks'

// Root component that contains all other components
function App() {

  // States are stored in the top component (to use them everywhere) and passed to components through props (Can also use other tools like Redux, Context API...)
  const [tasks, setTasks] = useState([])

  // Delete Task (a function passed to Tasks component)
  const deleteTask = (id) => {
    console.log('delete', id)
  }

  return (
    <div className="container">
      <Header />
      <Tasks tasks={tasks} onDelete={deleteTask} />
    </div>
  )
}

export default App;
